import React from 'react'

import featured_products from '../data/featured-products.json';


export const Grid = () => {

    const array = Object.entries(featured_products);
    const imagenes= array[7][1];

    const dataGrid = imagenes.map(item =>{
        return item.data
    })

  return (
    <>
              Grid -  featured-products - For each element on this grid, 
      you should show at least the 
      -main image of the product, 
      -its name, 
      -category, 
      -and price.

      {
          dataGrid.map(img =>{
              return(
                  <div>
                      <img src={img.mainimage.url} />
                  </div>
              )
          })
      }
    </>
  )
}
