import React,{useState} from 'react';
import '../estilos/ProductList.css'
import product_categories from '../data/product-categories.json';
import products from '../data/products.json';






const SideBar = () =>{
  let value='';

  const handleVal = (e) =>{
      console.log(e.target.innerText)
      value=e.target.innerText.toUpperCase();
  }
  const arrayCategories = Object.entries(product_categories);
  const categories= arrayCategories[7][1];
  const dataSideBar = categories.map(item =>{
    // item.data.name 
      return item.data 
  })

  return (
    <>
        <div class="sidebar">
          {dataSideBar.map(item => 
            <div value={item.name} onClick={handleVal}>{item.name}</div>
          )}
        </div>
        {
          <Grid valor=''/>
        }
    </>
  )
}


const Grid = ({valor='furniture'}) => {

  const arrayProducts = Object.entries(products);
  const productos= arrayProducts[7][1];
  console.log(valor.toUpperCase());

  return (
    <>
      <div class="grid-container">
        {
            productos.map(img =>{
              if(img.data.category.slug.toUpperCase() ==valor.toUpperCase()){
                return(
                  <div class="grid-item">
                      <img className='imagenTamanio' key={img.data.sku} src={img.data.mainimage.url} alt=""/>
                      <div>
                          {img.data.name}                          
                      </div>
                      <div>
                          {img.data.category.slug}
                      </div>
                      <div>
                          {img.data.price} $
                      </div>
                  </div>
                )
              }else{
                return(<></>)
              }           
            })
        }
      </div>
    </>
  )
}


export const ProductList = () => {
  return (
    <>
      <div>

        <SideBar/>
        <Grid/>

      </div>

    </>
  )
}
