import './App.css';
import { useFeaturedBanners } from './utils/hooks/useFeaturedBanners';


import featured_products from './data/featured-products.json';
import product_categories from './data/product-categories.json';
import products from './data/products.json';
import { Header } from './components/Header';
import { Slider } from './components/Slider';

function App() {
  const { data, isLoading } = useFeaturedBanners();


  return (
    <>
      <Header/>    
      slider - featured-banners
      carousel - product-categories
      Grid -  featured-products - For each element on this grid, 
      you should show at least the main image of the product, its name, category, and price.
      <Slider/>

    </>
  );
}

export default App;
